import React, {  useEffect, useState} from "react";
import { useHistory } from "react-router-dom";
//import CourseService from "../services/course.service";
import CourseService from "../services/course.service";

const CourseComponent = (props) => {
    let { currentUser, setCurrentUser } = props;
    const history = useHistory();
    const handleTakeToLogin = () => {
        history.push("/login");
    };
    let [courseData, setCourseData] = useState(null);
  useEffect(() => {
    console.log("Using effect.");
    let _id;
    if (currentUser) {
      _id = currentUser.user._id;
    } else {
      _id = "";
    }
    CourseService.get(_id).then((data) => {
      console.log(data);
      setCourseData(data.data);
  }).catch((err) => {
      console.log(err)
  });
 }, []);
  /*/
    if (currentUser.user.role == "instructor") {
      CourseService.get(_id)
        .then((data) => {
          console.log(data);
          setCourseData(data.data);
        })
        .catch((err) => {
          console.log(err);
        });
    } else if (currentUser.user.role == "student") {
      CourseService.getEnrolledCourses(_id)
        .then((data) => {
          console.log(data);
          setCourseData(data.data);
        })
        .catch((error) => {
          console.log(error);
        });
    }
  }, []);
  /*/
    /*/
    let [courseData, setCourseData] = useState(null);
    useEffect(() => {
        console.log("Using effect.");
        let _id;
        if (currentUser) {
            _id = currentUser.user._id;
        } else {
            _id = "";
        }
        CourseService.get(_id).then((data) => {
            setCourseData(data);
        }).catch((err) => {
            console.log(err)
        });
    }, []);
/*/
    return (
        <div style={{ padding: "3rem" }}>
            {!currentUser && ( <div>
                <p> You must login before seeing your course.</p>
                <button onClick={handleTakeToLogin}className="btn btn-primary btn-lg"> 
                Take me to login page.
                </button>
                </div>
                )}
                {currentUser && currentUser.user.role == "instructor" && (<div>
                        <h1>Welcom to instructor's Course page.</h1>
                    </div>
                )}
                {currentUser && courseData && courseData.length != 0 &&(
                  <div>
                    <p>Here's the data we got back from server.</p>
                 {courseData.map((course) => (
                  <div className="card" style={{width: "18rem"}}>
                    <div className="card-body">
                      <h5 className="card-title">{course.title}</h5>
                      <p className="card-text">{course.description}</p>
                      <p>Student Count: { course.student.length}</p> 
                      <button className="btn btn-primary">{course.price}</button>
                      <br />
                      </div>
                  </div>
                ))}
                </div>
              )}
                 {currentUser && currentUser.user.role == "student" && (<div>
                        <h1>Welcom to student's Course page.</h1>
                    </div>
                )}
        </div>
    );
};

export default CourseComponent;
